import React from "react";
import Footer from "./main/Footer";
import Header from "./main/Header";
import Notes from "./main/Notes";
import testnotes from "./testnotes";



function App() {
    return (
    <div>
        <Header />

        {/* 1. use props to create custom notes component
        2. createnotes function
        2. use mapping to map all notes with just one line. rmb the key */}
        {/* {testnotes.map(createNotes)} */}

        {/* optional: ES6 formatting */}
        {testnotes.map((noteList) =>  
        <Notes 
        key={noteList.key} 
        title={noteList.title} 
        content={noteList.content} />
        )}

        <Footer />
    </div>
    );
}

export default App;
